// RemoveFlight relationship.
// Sets o.R.Flight to nil.
// Removes o from all passed in related items' relationships struct.
func (o *FlightComment) RemoveFlight(ctx context.Context, exec boil.ContextExecutor, related *FlightRequestNew) error {
	var err error

	queries.SetScanner(&o.FlightID, nil)
	if _, err = o.Update(ctx, exec, boil.Whitelist("flight_id")); err != nil {
		return errors.Wrap(err, "failed to update local table")
	}

	if o.R != nil {
		o.R.Flight = nil
	}
	if related == nil || related.R == nil {
		return nil
	}

	for i, ri := range related.R.FlightFlightComments {
		if queries.Equal(o.FlightID, ri.FlightID) {
			continue
		}

		ln := len(related.R.FlightFlightComments)
		if ln > 1 && i < ln-1 {
			related.R.FlightFlightComments[i] = related.R.FlightFlightComments[ln-1]
		}
		related.R.FlightFlightComments = related.R.FlightFlightComments[:ln-1]
		break
	}
	return nil
}

