// FindJourney retrieves a single record by ID with an executor.
// If selectCols is empty Find will return all columns.
func FindJourney(ctx context.Context, exec boil.ContextExecutor, iD string, selectCols ...string) (*Journey, error) {
	journeyObj := &Journey{}

	sel := "*"
	if len(selectCols) > 0 {
		sel = strings.Join(strmangle.IdentQuoteSlice(dialect.LQ, dialect.RQ, selectCols), ",")
	}
	query := fmt.Sprintf(
		"select %s from \"journey\" where \"id\"=$1", sel,
	)

	q := queries.Raw(query, iD)

	err := q.Bind(ctx, exec, journeyObj)
	if err != nil {
		if errors.Is(err, sql.ErrNoRows) {
			return nil, sql.ErrNoRows
		}
		return nil, errors.Wrap(err, "dbmodels: unable to select from journey")
	}

	if err = journeyObj.doAfterSelectHooks(ctx, exec); err != nil {
		return journeyObj, err
	}

	return journeyObj, nil
}

